<div class="wrapper"><div class="menu top"><div class="img-flex" style="margin:0 10px 10px 0"><img src="<?php echo $data['image']; ?>?resize=80,80"/></div><h1><?php echo $data['artist']; ?> - <?php echo $data['title']; ?></h1><div class="small"><i>Upload on <?php echo timeline($data['added']); ?></i></div></div></div><div class="wrapper"><div class="kiri"><div class="menu"><h3 class="title-class">File Info</h3><p> Artist: <b><?php echo $data['artist']; ?></b></p><p> Title: <b><?php echo $data['title']; ?></b></p><p> Album: <b><?php echo $data['album']; ?></b></p><p> Genre: <b><?php echo $data['genre']; ?></b></p><p> Released: <b><?php echo timeline($data['date']); ?></b></p><p> Size: <b><?php echo $data['size']; ?></b></p><p> Duration: <b><?php echo $data['duration']; ?></b></p><br><div id="playing" align="center"><p style="padding:0 0 10px;"><a class="download" href="<?php echo convert_permalink($result[0]['id']); ?>" rel="nofollow noopener noreferrer">Download MP3</a><br></p></div></div><div class="menu"><h3 class="title-class">Lirik lagu <?php echo $data['artist']; ?> - <?php echo $data['title']; ?></h3><div id="search-result"><script> let query = "<?php echo $data['artist']; ?> - <?php echo $data['title']; ?>"; </script> <script> let result = document.getElementById("search-result"); function showCard(e) { return `${e.lyric}`; } document.addEventListener("DOMContentLoaded", function() { fetch("/grab/lyric.php?s=" + query).then((response) => { if (response.ok) { return response.json(); } else { console.log(response); return result.innerHTML = ` <h2 class="judul"> OOpss.. </h2> Ada masalah dengan pencarian <br/> "<strong>${query}</strong>" <br/> Error ${response.status} ${response.statusText}<br/> <h2 class="judul"> Cari dengan pencarian lain <</h2> `; } }).then(e => { t = "", e.forEach(e => { t += showCard(e) }), result.innerHTML = ` ${t}`;}) }) ;</script></div></div></div><div class="kanan"><div class="menu"><?php if ( $by_artist ) { ?><h3 class="title-class">Lagu <strong><?php echo $data['artist']; ?></strong> lainnya</h3><?php foreach ( $by_artist as $item ) { ?><div class="track"><div class="img-flex"></div><div class="item-flex"><h3 class="title"><a href="<?php echo post_permalink( $item['slug'] ); ?>" title="<?php echo htmlentities( $item['artist'].' - '.$item['title'], ENT_QUOTES ); ?> mp3 lyrics"><?php echo $item['artist']; ?> - <?php echo $item['title']; ?></a></h3><div class="small"><span><?php echo timeline($item['added']); ?></span></div></div></div><?php } unset( $item ); ?><?php } ?></div></div><div class="clear"></div></div>